mapper.select:
SELECT
eq.idEquipo AS eq_id, eq.nombre AS eq_nombre, eq.descripcion AS eq_descripcion, eq.disponible AS eq_disponible, eq.dar_baja AS eq_darBaja
FROM equipos AS eq WHERE #{id} = eq.idEquipo;

SELECT
eq.idEquipo AS eq_id, eq.nombre AS eq_nombre, eq.descripcion AS eq_descripcion, eq.disponible AS eq_disponible, eq.dar_baja AS eq_darBaja
FROM equipos AS eq;

SELECT
eq.idEquipo AS eq_id, eq.nombre AS eq_nombre, eq.descripcion AS eq_descripcion, eq.disponible AS eq_disponible, eq.dar_baja AS eq_darBaja
FROM equipos AS eq WHERE disponible = true;

mapper.update:
UPDATE equipos 
  SET dar_baja = TRUE, disponible = FALSE
WHERE idequipo=#{idEqui};

UPDATE elementos
  SET eqps = #{idEqui}, disponible = 0
  WHERE idElemento = #{idElem};
